import React, { Component } from 'react';

class CreateEmployeeComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            firstName: "",
            lastName: "",
            email: ""
        }

    }

    changeFirstNameHandler(event) {
        this.setState({ firstName: event.target.value });
    }
    changelastNameHandler(event) {
        this.setState({ lastName: event.target.value });
    }
    changeEmailAddressHandler(event) {
        this.setState({ emailAddres: event.target.value });
    }

    render() {
        return (
            <div>
                <div className='container'>
                    <div className='row'>
                        <div className='car col-md-6 offset-md-3'>
                            <h3 className='text-center'>Add Employee</h3>
                            <div className='card-body'></div>
                            <form>
                                <div className='form-group'>
                                    <label>First Name: </label>
                                    <input placeholder='First Name' name='firstName' className='form-control' value={this.state.firstName}
                                        onChange={this.changeFirstNameHandler} />
                                </div>
                                <div className='form-group'>
                                    <label>Last Name: </label>
                                    <input placeholder='Last Name' name='lastName' className='form-control' value={this.state.lastName}
                                        onChange={this.changelastNameHandler} />
                                </div>
                                <div className='form-group'>
                                    <label>Email Address: </label>
                                    <input placeholder='Email Address' name='emailAddress' className='form-control' value={this.state.emailAddres}
                                        onChange={this.changeEmailAddressHandler} />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateEmployeeComponent;
